    const router = useRouter()
    const project = router.query.project;

    // let content;

    const projectDetails = [
        {
        title: 'logistics-software',
        subtitle: 'SOFTWARE | WEBSITES | APPS',
        images: [{name: 'mobile-interfaces.png', width: 1200, height: 450}, {name: 'dashboard-screen.png', width: 1200, height: 700}],
        description: ''
        }
    ]

    const showContent = () => {


        // const selectedContent = projectDetails.find(item => item.title === project);

        if(project){
            const content =  projectDetails.map(item => {
                return <div>
                <div className="wittwer_imgs_wrapper">
                        <div>
                            <a style={{color: 'gray'}} href="ux-ui.php"><span className="glyphicon glyphicon-th-large project-btn" style={{color: 'aqua'}}></span> UI/UX Projects</a>
                        </div>

                        {
                            item.images.map(img => {
                            <h2>{img.name}</h2>
                                // <div className="interface_img">    
                                //     <center>
                                //         <Image
                                //         src={`/../public/${img.name}`}
                                //         alt="Picture of the author"
                                //         width={img.width}
                                //         height={img.height}
                                //         />
                                // </center>
                                // </div>
                            })
                        }


                       {/*  <div className="interface_img">    
                            <center>
                                <Image
                                   src="/../public/mobile-interfaces.png"
                                   alt="Picture of the author"
                                   width={1200}
                                   height={450}
                                   />
                           </center>
                        </div>

                        <div className="interface_img">    
                            <center>
                                <Image
                                   src="/../public/dashboard-screen.png"
                                   alt="Picture of the author"
                                   width={1200}
                                   height={700}
                                   />
                           </center>
                        </div>

                        <div className="interface_img">    
                            <center>
                                <img src="/images/messages-screen.png"/>
                           </center>
                        </div>

                        <div className="interface_img">    
                            <center>
                                <img src="/images/tracking-screen.png"/>
                           </center>
                        </div>

                        <div className="interface_img">    
                            <center>
                                <img src="/images/customs-screen.png"/>
                           </center>
                        </div> */}

            </div>


                <div className="projectDescription dark-gray">
                    <h2>PROJECT DETAILS</h2>
                   <hr/> 

                    <p>The logistics software is a web based platform that tracks cargo shipped through land, sea, water, and air.  Providing information such as expected dates, current locations, cargo item details, shipping history statistics, routing information, document tracking, communication capabilities between all parties, and more. The customers who see this software for the first time often say "it's futuristic!". </p>
                </div> 


            </div>
            })
            return content;

         }else{
             return <p>loading...</p>
         }

    }



    return (
        <div className='porject'>
            <div className="primaryBlue-banner">
                <h1 className='mainTitle'>{project}</h1>
                <h3>SOFTWARE | WEBSITES | APPS</h3>
            </div>
            {/* {
                projectDetails.map(item => {
                <h1>{item.title}</h1>
                })
            } */}

            {showContent()}

            <Button onClick={() => router.back()}>BACK</Button>



        </div>
    )
}

export default Project;
